'''
def deep_copy(s):
    new_list = list(); 
    for i in s:
        new_i=list()
        for j in i:
            new_i.append(j)
        new_list.append(new_i)
    return new_list
    '''

def deep_copy(s):
    new_list = []
    for i in range(len(s)):
        new_list+=[copy(s[i])]
    return new_list
