def fitness_check(solution,data): 
    #check if there is cache server which is full 
    for i in range(data["number_of_caches"]):
        sum_size=0
        for j in range(data["number_of_videos"]):
            sum_size+=solution[i][j]*data["video_size_desc"][j]
        if sum_size>data["cache_size"]:
            #print("the video size in the cache server is too big")
            return False
    return True
        
def fitness(solution,data): 
    sum_requests = 0
    for i in data["video_ed_request"]:
        sum_requests += int(data["video_ed_request"][i])
    
    saving_time = 0
    for key in data["video_ed_request"]:
        #For every request 
        request_video=int(data["video_ed_request"][key])
        endpoint=int(key[1])
        dc_time=data["ep_to_dc_latency"][endpoint]
        cache_latency_list=data["ep_to_cache_latency"][endpoint]
        #print("the cache latency is",cache_latency_list)
        
        #add all the available cache server's latency to cache_time_list
        cache_time_list=[dc_time,]
        for i in range(data["number_of_caches"]):
            if(solution[i][int(key[0])]==1): 
                #print(cache_latency_list[i])
                cache_time_list.append(cache_latency_list[i])               
        
        cache_time=min(cache_time_list)
        
        time_request=request_video*(dc_time-cache_time)
        #print("the saving time is",time_request)
        saving_time+=time_request
    scores=(saving_time/sum_requests)*1000
    #print("saving time is ",saving_time)
    #print("the sum request is ", sum_requests)
    #print("average time ",avg_time)
    return scores

def plus_one(sol):
    sol_add=deep_copy(sol)
    for i in range(data["number_of_caches"]):
        for j in range(data["number_of_videos"]):
            sol_new=deep_copy(sol)
            #print("the solution before is ",sol)
            if(sol_new[i][j]==0):
                sol_new[i][j]=1
                #print("the new solution is",sol_new)
                if(fitness_check(sol_new,data) and fitness(sol_new,data)>fitness(sol_add, data)):
                    sol_add=deep_copy(sol_new) 
    
    #print("the plus_one sol is ",sol_add)
    return sol_add
     
def minus_one(sol):
    sol_minus=deep_copy(sol)  
    for i in range(data["number_of_caches"]):
        for j in range(data["number_of_videos"]):
            sol_new=deep_copy(sol)
            if(sol_new[i][j]==1):
                sol_new[i][j]=0
                
                sol_new=deep_copy(plus_one(sol_new))
                if(fitness_check(sol_new,data) and fitness(sol_new,data)>fitness(sol_minus, data)):
                    sol_minus=deep_copy(sol_new)
            else:
                continue
    
    #print("the minus_one sol is ",sol_minus) 
    return sol_minus
def find_solution(data):
    s=[ [random.randint(0,1) for _ in  range(data["number_of_videos"])] for _ in range(data["number_of_caches"])]
    #the number of cache server and video 
    end_p=data["number_of_caches"]
    video=data["number_of_videos"]
    #print(end_p,video)
    #check after remove an video from a cache server, if the solution works 
    while(not fitness_check(s, data)):
        for i in range(end_p):
            sum_size=0
            #check the sum_size of the video in the cache server 
            for j in range(video):
                sum_size+=s[i][j]*data["video_size_desc"][j]
            while(sum_size>data["cache_size"]):
                x=random.randint(0,video-1)
                while(s[i][x]==0):
                    x=random.randint(0,video-1)
                s[i][x]=0
                sum_size=0
                for j in range(video):
                    sum_size+=s[i][j]*data["video_size_desc"][j]
                
            #print(s)
            #print("the video size in the cache server is too big")       
    #print("The random solution is ",s)
    return s

def hill_climbing(s,data):
    solution=s
    improvement=True
    n=0
    while(improvement):
        plus_sol=deep_copy(plus_one(solution))
        if(fitness(plus_sol,data)>fitness(solution, data)):   
            solution=deep_copy(plus_sol)
            n+=1
            #print("+1",n)
            #print("+",solution)   
        #elif(fitness(minus_one(solution),data)>fitness(solution, data)):
            #solution=deep_copy(minus_one(solution))
            #print("-1",n)
            #print("-",solution)
        else:
            improvement=False
    return solution
            
        
